import { Link } from 'react-router-dom'
import React, { useState, useEffect } from 'react'

const Stopwatch = () => {
    // state to store solve time
    const [time, setTime] = useState(0)

    // state to check stopwatch running or not
    const [isRunning, setIsRunning] = useState(false)

    useEffect(() => {
        let intervalId

        if (isRunning) {
            // setting time from 0 to 1 every 10 ms using javascript setInterval met
            intervalId = setInterval(() => setTime(time + 1), 10)
        }

        return () => clearInterval(intervalId)
    }, [isRunning, time])

    // hours calculation
    const hours = Math.floor(time / 360000)

    // minutes calculation
    const minutes = Math.floor((time % 360000) / 6000)

    // seconds calculation
    const seconds = Math.floor((time % 6000) / 100)

    // milliseconds calculation
    const milliseconds = time % 100

    // reset and start stopwatch with spacebar
    useEffect(() => {
        const handleKeyDown = (e) => {
            if (!isRunning && e.key === " " && !keyPressTime) {
                const startTime = Date.now()
                setKeyPressTime(startTime)

                const newTimer = setTimeout(() => {
                    if (Date.now() - startTime >= 1000) {
                        reset()
                        start()
                    }
                }, 1000)

                setTimer(newTimer)
            }
        }

        const handleKeyUp = (e) => {
            if (e.key === " ") {
                setKeyPressTime(null)
                if (timer) {
                    clearTimeout()
                    setTimer(null)
                }
            }
        }

        document.addEventListener('keydown', handleKeyDown)
        document.addEventListener('keyup', handleKeyUp)

        return () => {
            document.removeEventListener('keydown', handleKeyDown)
            document.removeEventListener('keyup', handleKeyUp)
        }
    }, [keyPressTime, timer])

    // stop stopwatch with any key
    useEffect(() => {
        const handleKeyDown = () => {
            if (isRunning) {
                stop()
            }
        }

        document.addEventListener('keydown', handleKeyDown)

        return () => {
            document.removeEventListener('keydown', handleKeyDown)
        }
    })

    // state to measure duration of a key pressed down
    const [keyPressTime, setKeyPressTime] = useState(null)

    // state to determine if a key is pressed long enough
    const [timer, setTimer] = useState(null)

    // method to start stopwatch
    const start = () => {
        setIsRunning(true)
    }

    // method to stop stopwatch
    const stop = () => {
        setIsRunning(false)
    }

    // method to reset stopwatch
    const reset = () => {
        setTime(0)
    }

    return (
        <div className='stopwatch-container'>
            <p className='stopwatch-time'>
                {hours}:{minutes.toString().padStart(2, "0")}:{seconds.toString().padStart(2, "0")}.{milliseconds.toString().padStart(2, "0")}
            </p>
            <div className='stopwatch-buttons'>
                <button className='stopwatch-button' onClick={() => {
                    if (!isRunning) {
                        console.log(hours + ":" + minutes.toString().padStart(2, "0") + ":" + seconds.toString().padStart(2, "0") + "." + milliseconds.toString().padStart(2, "0"))
                    }
                }}>
                    Show
                </button>
            </div>
        </div>
    )
}

export default Stopwatch
