import { Link } from 'react-router-dom'
import React, { useState, useEffect, useRef } from 'react'

const Stopwatch = () => {
    // state to store solve time
    const [time, setTime] = useState(0)

    // state to check stopwatch running or not
    const [isRunning, setIsRunning] = useState(false)

    useEffect(() => {
        let intervalId

        if (isRunning) {
            // setting time from 0 to 1 every 10 ms using javascript setInterval met
            intervalId = setInterval(() => setTime(time + 1), 10)
        }

        return () => clearInterval(intervalId)
    }, [isRunning, time])

    // hours calculation
    const hours = Math.floor(time / 360000)

    // minutes calculation
    const minutes = Math.floor((time % 360000) / 6000)

    // seconds calculation
    const seconds = Math.floor((time % 6000) / 100)

    // milliseconds calculation
    const milliseconds = time % 100

    // ref to store timestamp when spacebar is pressed down
    const spacebarDownTime = useRef(null)

    useEffect(() => {
        // const handleKeyDown = (event) => {
        //     if (event.key === " " && spacebarDownTime.current === null) {
        //         spacebarDownTime.current = Date.now()
        //     } else {
        //         setIsRunning(false)
        //     }
        // }

        // const handleKeyUp = (event) => {
        //     if (event.key === " " && spacebarDownTime.current) {
        //         const pressDuration = Date.now() - spacebarDownTime.current

        //         if (pressDuration >= 1000) {
        //             setTime(0)
        //             setIsRunning(true)
        //         }

        //         spacebarDownTime.current = null
        //     }
        // }

        const handleKeyDown = (event) => {
            if (event.key === " " && spacebarDownTime.current === null) {
                spacebarDownTime.current = Date.now()
            }
        }

        const handleKeyUp = (event) => {
            if (event.key === " " && spacebarDownTime.current !== null) {
                const pressDuration = Date.now() - spacebarDownTime.current

                if (pressDuration >= 1000) {
                    setTime(0)
                    setIsRunning(true)
                } else {
                    setIsRunning(prevState => !prevState)
                }

                spacebarDownTime.current = null
            } else if (event.key !== " ") {
                setIsRunning(false)
            }
        }

        window.addEventListener('keydown', handleKeyDown)
        window.addEventListener('keyup', handleKeyUp)

        return () => {
            window.removeEventListener('keydown', handleKeyDown)
            window.removeEventListener('keyup', handleKeyUp)
        }
    })

    return (
        <div className='stopwatch-container'>
            <p className='stopwatch-time'>
                {hours}:{minutes.toString().padStart(2, "0")}:{seconds.toString().padStart(2, "0")}.{milliseconds.toString().padStart(2, "0")}
            </p>
            <div className='stopwatch-buttons'>
                <button className='stopwatch-button' onClick={() => { setIsRunning(true) }}>
                    Start
                </button>
                <button className='stopwatch-button' onClick={ () => {setIsRunning(false)} }>
                    Stop
                </button>
                <button className='stopwatch-button' onClick={ () => {setTime(0)} }>
                    Reset
                </button>
                <button className='stopwatch-button' onClick={() => {
                    if (!isRunning) {
                        console.log(hours + ":" + minutes.toString().padStart(2, "0") + ":" + seconds.toString().padStart(2, "0") + "." + milliseconds.toString().padStart(2, "0"))
                    }
                }}>
                    Show
                </button>
            </div>
        </div>
    )
}

export default Stopwatch
