import { Link } from 'react-router-dom'
import React, { useState, useEffect } from 'react'

const Stopwatch = () => {
    // state to store time
    const [time, setTime] = useState(0)

    // state to check stopwatch running or not
    const [isRunning, setIsRunning] = useState(false)

    useEffect(() => {
        let intervalId

        if (isRunning) {
            // setting time from 0 to 1 every 10 ms using javascript setInterval met
            intervalId = setInterval(() => setTime(time + 1), 10)
        }

        return () => clearInterval(intervalId)
    }, [isRunning, time])

    // hours calculation
    const hours = Math.floor(time / 360000)

    // minutes calculation
    const minutes = Math.floor((time % 360000) / 6000)

    // seconds calculation
    const seconds = Math.floor((time % 6000) / 100)

    // milliseconds calculation
    const milliseconds = time % 100

    // method to start and stop stopwatch
    const startAndStop = () => {
        setIsRunning(!isRunning)
    }

    // method to reset stopwatch to 0
    const reset = () => {
        setTime(0)
    }

    return (
        <div className='stopwatch-container'>
            <p className='stopwatch-time'>
                {hours}:{minutes.toString().padStart(2, "0")}:{seconds.toString().padStart(2, "0")}.{milliseconds.toString().padStart(3, "0")}
            </p>
            <div className='stopwatch-buttons'>
                <button className='stopwatch-button' onClick={startAndStop}>
                    {isRunning ? "Stop" : "Start"}
                </button>
                <button className='stopwatch-button' onClick={reset}>
                    Reset
                </button>
            </div>
        </div>
    )
}

export default Stopwatch
